export const currentUserReducer = (state = {}, action) => {
    if (action.type == 'edit-current-user-name') {
        return {
            name: action.payload.name
        }
    }
    return state;
}

export const currentUserData = {
    name: 'Joe shmoe'
}

export const selectName = (state) => {
    return state.currentUser.name;
}
