import {createStore} from "redux";

const currentUserReducer = (state={}, action) => {
    if (action.type == 'edit-current-user-name') {
        return {
            ...state,
            currentUser: {
                name: action.payload.name
            }
        }
    }
}

const store = createStore((state, action) => {

    return state;
}, {
    friends: [],
    posts: [],
    currentUser: {
        name: 'Joe shmoe'
    },
    todos: [
        {
            id: Math.random(),
            text: 'Learn JS',
            isCompleted: false
        },
        {
            id: Math.random(),
            text: 'Learn html',
            isCompleted: false
        },
        {
            id: Math.random(),
            text: 'Learn Css',
            isCompleted: false
        }
    ],
});

export default store;
