import {createStore} from "redux";

const store = createStore((state, action) => {
    if (action.type == 'edit-current-user-name') {
        return {
            ...state,
            currentUser: {
                name: action.payload.name
            }
        }
    }
    return state;
}, {
    currentUser: {
        name: 'Joe shmoe'
    }
});

export default store;
